function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '')
		return;

	var gr = new GlideRecord('x_306552_slide_pro_rental');
// 	var userSysId = this.getParameter('sysparm_sys_userID');

	gr.addQuery('u_status', 'active');
	gr.addQuery('u_status', 'overdue');
	gr.query();

	while(gr.next()){
		jslog("PETLAAAA = " + newValue);

		if(gr.u_reference_1.sys_id == newValue.sys_id) {
			return true;
		}
	}
	return false;
}




