sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') 
      return;

	var milisecToDay = 1000/60/60/24;
	var date_start = getDateFromFormat(g_form.getValue('u_glide_date_3'), 'yyyy-MM-dd');
	var date_end = getDateFromFormat(newValue, 'yyyy-MM-dd');
	var milisec = date_end-date_start;
 	var day = milisec/milisecToDay;

	while(g_form.getValue('u_glide_date_3') == "") {
		g_form.setDisabled('u_glide_date_4', true);
	}
	if(day>7)
		alert('Maximum time limit for rental shall be seven days');
		g_form.setValue('u_glide_date_4',"");


}
