var CheckUserRental = Class.create();
CheckUserRental.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	myFunction: function() {
		var gr = new GlideRecord('x_306552_slide_pro_rental');
		var userSysId = this.getParameter('sysparm_sys_userID');
		gs.error("INCLUDEEEEEEEEEEEEEEEEEE" + userSysId);

		gr.addQuery('u_status', 'active');
		gr.addQuery('u_status', 'overdue');
		gr.query();

		while(gr.next()){
			if(gr.u_reference_1.sys_id == userSysId.sys_id) {
				return true;
			}
		}
		return false;
	},
	type: 'CheckUserRental'
});
