sysauto_script.script:
var gr = new GlideRecord('x_306552_slide_pro_rental');
var rightNow = new GlideDateTime();
var twoDays = new GlideDateTime();
twoDays.addDaysLocalTime(2);

gr.addQuery('u_status', 'active');
gr.query();

while(gr.next()){
	var endDate = gr.u_glide_date_4;

	//change status rental on 'overdue'
	//send notfications to users of overdue rental
	if(endDate <= rightNow) {
		gr.u_status = 'overdue';
		gs.eventQueue('x_306552_slide_pro.slide.projector regis',gr,gr.u_reference_1.sys_id ,gr.number);

	}

	//send notifications to users two days before the deadline.
	if(endDate == twoDays.getLocalDate()) {
		gs.eventQueue('x_306552_slide_pro.2 days reminder',gr,gr.u_reference_1.sys_id ,gr.number);

	}
}
